DROP TABLE IF EXISTS "Artikel";
CREATE TABLE "Artikel" ("aid" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL UNIQUE, "artikelNr" INTEGER ,  "author" VARCHAR(30), "titel" VARCHAR(30), "preis" DOUBLE, "status" BOOL, "bestand" INTEGER, "warengruppe" INTEGER);
DROP TABLE IF EXISTS "Kassenvorgang";
CREATE TABLE "Kassenvorgang" ("pk" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "kvNr" INTEGER ,  "kassenvorgangID" INTEGER NOT NULL, "aid" INTEGER NOT NULL , "kid" INTEGER NOT NULL , "anzahl" INTEGER NOT NULL , "datum" DATETIME NOT NULL , "storno" BOOL NOT NULL );
DROP TABLE IF EXISTS "Kunde";
CREATE TABLE "Kunde" ("kid" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE ,  "kundeNr" INTEGER , "vorname" VARCHAR (30), "nachname" VARCHAR (30), "strasse" VARCHAR (30), "hausnr" VARCHAR (5), "plz" VARCHAR (10), "stadt" VARCHAR (30), "email" VARCHAR (40), "telefonNr" VARCHAR (20), "zahlungsart" INTEGER(1), "status" BOOL);
